import { getHeroeByid } from './data/heroesFuntions';

const promesa = new Promise((resolve, reject) => {
    setTimeout(() => {
        const heroe = getHeroeByid(2);
        resolve(heroe);
    }, 2000);
});

promesa.then((heroe) => {
    console.log(heroe);
});
//console.log(getHeroeByid(2), getHeroeByOwner('Marvel'));
